using Microsoft.EntityFrameworkCore;
namespace Task5.Pages.Entities
{
    public class FilmsContext : DbContext
    {
        public DbSet<Film> Films { get; set; }

        public FilmsContext(DbContextOptions<FilmsContext> options)
            : base(options)
        {
           
            Database.EnsureCreated();

            if (Films.ToList().Count == 0)
            {
                Films.AddRange(new List<Film>()
                {
                     new()
                     {
                         Name = "Iron Man",
                         ImageUrl =  "data:image/jpeg;base64,",
                         Producer = "Kevin Feige",
                         Style = "Action",
                         PublishingYear = 2008,
                         Description = "Tony Stark is charismatic and eccentric, he is also very proud, but also very altruist and heroic. He did not have a good childhood, and this affects how he acts. Stark is very smart, and a very talented inventor. His technology is always very advanced, and Iron Man is his greatest piece of work.",
                         Seances = new List<Seance>()
                         {
                             new Seance(DateTime.Now.AddHours(3), DateTime.Now.AddHours(5)),
                             new Seance(DateTime.Now.AddHours(1), DateTime.Now.AddHours(3))
                         }
                     },
                     new()
                     {
                         Name = "Iron Man 2",
                         ImageUrl = "data:image/jpeg;base64,",
                         Producer = "Kevin Feige",
                         Style = "Action",
                         PublishingYear = 2010,
                         Description = "After Tony Stark announces to the World that he's Iron Man. He faces many problems in his Life, His Lifesource is Poisoning him, The US Government wants his tech, and someone's out to kill Stark. That Someone is Ivan Vanko, Son of Anton Vanko who worked with Tony's Father Howard, But Howard then sent Anton to Russia.",
                         Seances = new List<Seance>()
                         {
                             new Seance(DateTime.Now, DateTime.Now.AddHours(2)),
                             new Seance(DateTime.Now.AddHours(1), DateTime.Now.AddHours(3))
                         }
                     }

                });
                SaveChanges();
            }
               
        }

        protected override void OnModelCreating(ModelBuilder modelBuilder)
        {
            modelBuilder.Entity<Film>()
                .HasMany(b => b.Seances)
                .WithOne()
                .OnDelete(DeleteBehavior.Cascade);

        }
    }
}
